import React, { useState, useEffect } from 'react';
import { supabase } from "./lib/helper/supabaseClient.js";
import LoginPage from './login/Login.jsx';
import Login from './login/Login.jsx';

export default function App() {
  const [user, setUser] = useState(null);
  const [role, setRole] = useState(null);

  useEffect(() => {
    // Function to fetch role from PostgreSQL based on user ID
    const fetchUserRole = async (userId) => {
      const { data, error } = await supabase
        .from('user_roles') // Replace with your actual table name
        .select('role')
        .eq('user_id', userId);

      if (error) {
        console.error('Error fetching role:', error);
        return null;
      }

      return data[0]?.role; // Assuming only one role per user
    };

    const getUser = async () => {
      const { data: { user } } = await supabase.auth.getUser();
      if (user) {
        setUser(user);
        // Fetch role from the database (PostgreSQL)
        const userRole = await fetchUserRole(user.id);
        setRole(userRole);
      }
    };

    getUser();

    const { data: { subscription } } = supabase.auth.onAuthStateChange((_event, session) => {
      setUser(session?.user || null);
      if (session?.user) {
        fetchUserRole(session.user.id).then(setRole); // Fetch role on session change
      }
    });

    return () => {
      subscription.unsubscribe();
    };
  }, []);

  const login = async () => {
    await supabase.auth.signInWithOAuth({
      provider: "google",
    });
  };

  const logout = async () => {
    await supabase.auth.signOut();
    setUser(null);
    setRole(null);
  };

  return (
    <div>
      {user ? (
        <div>
          <p>Welcome, {user.user_metadata?.name || "User"}!</p>
          {role && <p>Your role: {role}</p>} {/* Display role fetched from PostgreSQL */}
          <p>Your user ID: {user.id}</p> {/* Display user ID */}
          <button onClick={logout}>Logout</button>
        </div>
      ) : (
        <div>
          <Login />
        </div>
      )}
    </div>
  );
}
