import React, { useState, useEffect } from 'react';
import { supabase } from "./lib/helper/supabaseClient.js";
import LoginPage from './login/Login.jsx';
import Login from './login/Login.jsx';

export default function App() {
  const [user, setUser] = useState(null);
  const [role, setRole] = useState(null);

  useEffect(() => {
    // Check for an authenticated user
    const getUser = async () => {
      const { data: { user } } = await supabase.auth.getUser();
      if (user) {
        setUser(user);
        // Assuming the role is in app_metadata
        setRole(user?.app_metadata?.role);
      }
    };

    getUser();
  
    const { data: { subscription } } = supabase.auth.onAuthStateChange((_event, session) => {
      setUser(session?.user || null);
      setRole(session?.user?.app_metadata?.role); // Update role on session change
    });
  
    return () => {
      subscription.unsubscribe();
    };
  }, []);

  const login = async () => {
    await supabase.auth.signInWithOAuth({
      provider: "google",
    });
  };

  const logout = async () => {
    await supabase.auth.signOut();
    setUser(null);
    setRole(null);
  };

  return (
    <div>
      {user ? (
        <div>
          <p>Welcome, {user.user_metadata?.name || "User"}!</p>
          {role && <p>Your role: {role}</p>} {/* Display role */}
          <button onClick={logout}>Logout</button>
        </div>
      ) : (
        <div>
          <Login />
        </div>
      )}
    </div>
  );
}
